import React, {forwardRef, useEffect, useState} from "react";
import { InputWrapper, SInput, SLabel } from "./Input.styled";

interface IInputProps {
    type?: string;
    value?: string;
    label?: string;
    checked?: boolean
}

const Input = forwardRef<HTMLInputElement, IInputProps>((props, ref) => {
    const [inputValue, setInputValue] = useState<string>('');
    useEffect(() => {
        props.value && setInputValue(props.value);
    }, []);

    useEffect(() => {
        console.log(ref)
    }, [ref])

    return (
        <InputWrapper>
            {(props.type === "radio" || props.label) && (
                <SLabel htmlFor={props.label}>{props.label}</SLabel>
            )}
            <SInput id={props.label} ref={ref} {...props} value={inputValue} />
        </InputWrapper>
    );
});

export default Input;
