import { ISurvey } from "../components/Survey/Survey";


export const addUpdateQuestion = (
    answer: string | string[],
    question: string | string[],
    survey: ISurvey
) => {
    if (Array.isArray(question) && Array.isArray(answer)) {
        question.forEach((questionItem, questionIndex) => {
            const qItem = questionItem as keyof typeof survey;
            if(survey[qItem]){
                survey[qItem] = answer[questionIndex];
            }
        })
    }
