import React, { FC } from "react";
import { ISurvey } from "../../types";
import { questions } from "../../consts";

interface IAnsweredQuestionsProps {
    answeredQuestions: ISurvey;
}
const AnsweredQuestions: FC<IAnsweredQuestionsProps> = ({
    answeredQuestions,
}) => {
    const answersAndQuestions = questions.map((q) => {
        let answer =
            answeredQuestions[q.fieldName as keyof typeof answeredQuestions];
        if (!answer) {
            // q.multiple?.reduce((acc, item) => answeredQuestions[item.fieldName as keyof typeof answeredQuestions], '')
            q.multiple
                ?.map(
                    (item) =>
                        answeredQuestions[
                            item.fieldName as keyof typeof answeredQuestions
                        ]
                )
                .join(", ");
        }
        console.log({ question: q.question, answer });
        return { question: q.question, answer };
    });
    return (
        <div
            style={{
                display: "flex",
                flexDirection: "column",
                justifyContent: "center",
                alignItems: "center",
            }}
        >
            {answersAndQuestions.map((item) => (
                <div style={{ outline: "1px solid teal", margin: "15px 0" }}>
                    <p>
                        <b>{item.question}</b>
                    </p>
                    <p>{item.answer}</p>
                </div>
            ))}
        </div>
    );
};

export default AnsweredQuestions;
