import React from "react";
import { createPortal } from "react-dom";
import { ModalContent, ModalWrapper } from "./Modal.styled";
import { SButton } from "../../Survey/Survey.styled";
import styled from "styled-components";

const BtnContainer = styled.div`
    display: flex;
    justify-content: center;
    gap: 50px;
`;

type ModalBtnProps = {btnType: string};

const ModalBtn = styled(SButton)<ModalBtnProps>`
    background-color: ${(props) => props.btnType === "danger" && "#a54500"};
    color: #fff;
`;

const Modal = () => {
    const portalElement = document.getElementById("portal");
    if (portalElement) {
        return createPortal(
            <ModalWrapper>
                <ModalContent>
                    <h2 style={{ textAlign: "center" }}>
                        Do you want to continue the survey after leaving the
                        site and returning again?
                    </h2>
                    <h2 style={{ textAlign: "center" }}>
                        Do you want to continue an incomplete survey?
                    </h2>
                    <BtnContainer>
                        <ModalBtn btnType="danger">No</ModalBtn>
                        <ModalBtn btnType="success">Yes</ModalBtn>
                    </BtnContainer>
                </ModalContent>
            </ModalWrapper>,
            portalElement
        );
    }

    return <></>;
};

export default Modal;
