import React from 'react';
import {createPortal} from "react-dom";
import {ModalContent, ModalWrapper} from "./Modal.styled";
import {SButton} from "../../Survey/Survey.styled";


const Modal = () => {
    const portalElement = document.getElementById('portal');
    if(portalElement){
        return createPortal(
                <ModalWrapper>
                    <ModalContent>
                        <h2 style={{textAlign: 'center'}}>Do you want to continue the survey after leaving the site and returning again?</h2>
                        <h2 style={{textAlign: 'center'}}>Do you want to continue an incomplete survey?</h2>
                        <div>
                            <SButton style={{backgroundColor: 'red'}}>No</SButton>
                            <SButton>Yes</SButton>
                        </div>
                    </ModalContent>
                </ModalWrapper>, portalElement
        );
    }

    return <></>;
};

export default Modal;
