import React, { Fragment, useEffect, useState } from "react";
import { useForm } from "react-hook-form";
import { yupResolver } from "@hookform/resolvers/yup";
import { QuestionTypes } from "../../store/types";
import Input from "../UI/Input/Input";
import { useQuestion } from "../../hooks";
import Select from "../UI/Select/Select";
import { QuestionTitle, SButton, Wrapper, ErrorMsg } from "./Survey.styled";
import Modal from "../UI/Modal/Modal";

type LSSurvey = { question: string; answer: string }[];

const Survey = () => {
    const [questionIndex, setQuestionIndex] = useState(0);
    const [answeredQuestions, setAnsweredQuestions] = useState<LSSurvey>([]);
    // const [continueSurvey, setContinueSurvey] = useState<boolean>(false);
    const [confirmSurvey, setConfirmSurvey] = useState<boolean>(false);
    const [modalOpen, setModalOpen] = useState<boolean>(false);
    const [modalTitle, setModalTitle] = useState<string>("");

    const { question, fieldName, validationSchema, questions } =
        useQuestion(questionIndex);

    type FormTypes = {
        [fieldName: string]: typeof question.currentValue | string;
    };

    const {
        register,
        formState: { errors },
        handleSubmit,
    } = useForm<FormTypes>({ resolver: yupResolver(validationSchema) });

    const onSubmit = (data: FormTypes) => {
        let survey = localStorage.getItem("survey");
        if (survey) {
            const parsedSurvey = JSON.parse(survey) as LSSurvey;
            const answerToPass = data[
                Object.keys(data)[questionIndex]
            ] as string;
            parsedSurvey.push({
                question: question.question,
                answer: answerToPass,
            });
            localStorage.setItem("survey", JSON.stringify(parsedSurvey));
        }
        setQuestionIndex((prev) => ++prev);
    };

    useEffect(() => {
        const survey = localStorage.getItem("survey");
        if (confirmSurvey) {
            if (survey) {
                const lsAnsweredQuestions = JSON.parse(survey) as LSSurvey;
                setAnsweredQuestions(lsAnsweredQuestions);
                setQuestionIndex(lsAnsweredQuestions.length);
            } else {
                localStorage.setItem("survey", JSON.stringify([]));
            }
        }else{
            localStorage.removeItem("survey");
        }
    }, [confirmSurvey]);

    useEffect(() => {
        const survey = localStorage.getItem("survey");
        let openModal: boolean;
        let modalTitle_: string;
        if (!survey) {
            openModal = true;
            modalTitle_ =
                "Do you want to continue the survey after leaving the site and returning again?";
        } else {
            openModal = true;
            modalTitle_ = "Do you want to continue an incomplete survey?";
        }
        setModalOpen(openModal);
        setModalTitle(modalTitle_);
    }, []);


    return (
        <>
            {!confirmSurvey && modalOpen && (
                <Modal
                    setModalOpen={setModalOpen}
                    modalTitle={modalTitle}
                    onCancel={() => {
                        setModalOpen(false);
                        setConfirmSurvey(false);
                    }}
                    onConfirm={() => {
                        setModalOpen(false);
                        setConfirmSurvey(true);
                    }}
                />
            )}
            {!modalOpen && <Wrapper>
                <h1 style={{marginBottom: "50px"}}>
                    Question {questionIndex + 1}/{questions.length}
                </h1>
                <QuestionTitle>{question.question}</QuestionTitle>
                <form onSubmit={handleSubmit(onSubmit)}>
                    {question.type === QuestionTypes.textInput && (
                        <Input type="text" {...register(fieldName)} />
                    )}
                    {question.type === QuestionTypes.numberInput && (
                        <Input type="number" {...register(fieldName)} />
                    )}
                    {question.type === QuestionTypes.date && (
                        <Input type="date" {...register(fieldName)} />
                    )}
                    {question.type === QuestionTypes.select && (
                        <Select
                            items={
                                Array.isArray(question.currentValue)
                                    ? question.currentValue
                                    : []
                            }
                            {...register(fieldName)}
                        />
                    )}
                    {question.type === QuestionTypes.radio &&
                        Array.isArray(question.currentValue) &&
                        question.currentValue.map((opt) => (
                            <Input
                                type="radio"
                                label={opt}
                                value={opt}
                                key={opt}
                                {...register(fieldName)}
                            />
                        ))}
                    {question.type === QuestionTypes.multiple &&
                        question?.multiple?.map((q) => (
                            <Fragment key={q.inputLabel}>
                                {q.type === QuestionTypes.email && (
                                    <Input
                                        label={q.inputLabel}
                                        type="email"
                                        {...register(q.fieldName)}
                                    />
                                )}
                                {q.type === QuestionTypes.textInput && (
                                    <Input
                                        label={q.inputLabel}
                                        {...register(q.fieldName)}
                                    />
                                )}
                                <p style={{color: "red"}}>
                                    {errors[q.fieldName]?.message}
                                </p>
                            </Fragment>
                        ))}
                    <ErrorMsg>{errors[fieldName]?.message}</ErrorMsg>
                    <div>
                        <SButton>Submit</SButton>
                    </div>
                </form>
            </Wrapper>}
        </>
    );
};

export default Survey;
