import React from "react";
import { createPortal } from "react-dom";
import { ModalContent, ModalWrapper } from "./Modal.styled";
import { SButton } from "../../Survey/Survey.styled";
import styled from "styled-components";

const BtnContainer = styled.div`
    display: flex;
    justify-content: center;
    gap: 50px;
`;

type ModalBtnProps = {btnType?: string};

const ModalBtn = styled(SButton)<ModalBtnProps>`
    background-color: ${(props) => props.btnType === "danger" && "var(--orange)"};
    color: #fff;
    &:hover{    
        outline: ${(props) => props.btnType === "danger" ? "1px solid var(--orange)" : "1px solid teal"};
        color: ${(props) => props.btnType === "danger" && "var(--orange)"};
    }
`;

const ModalTitle = styled.h2`
    text-align: center;
    color: var(--orange);
`

const Modal = () => {
    const portalElement = document.getElementById("portal");
    if (portalElement) {
        return createPortal(
            <ModalWrapper>
                <ModalContent>
                    <ModalTitle>
                        Do you want to continue the survey after leaving the
                        site and returning again?
                    </ModalTitle>
                    <ModalTitle>
                        Do you want to continue an incomplete survey?
                    </ModalTitle>
                    <BtnContainer>
                        <ModalBtn btnType="danger">No</ModalBtn>
                        <ModalBtn>Yes</ModalBtn>
                    </BtnContainer>
                </ModalContent>
            </ModalWrapper>,
            portalElement
        );
    }

    return <></>;
};

export default Modal;
