import {ISurvey} from "../components/Survey/Survey";

export const addUpdateQuestion = (answer: string, question: string, survey: ISurvey[]) => {
    const existingItemIndex = survey.findIndex(item => item.question === question);
    if(existingItemIndex === -1){
        survey.push({answer, question});
    }else{
        survey = survey.map(item => {
            if(item.question === question){
                return {answer, question};
            }
            return item;
        })
    }
    return survey;
}
