import {createSlice} from "@reduxjs/toolkit";
import {IQuestionInitialState, QuestionTypes} from "../types";
import * as yup from "yup";
import {question_2, question_3, question_4, question_8} from "../../consts";

const initialState: IQuestionInitialState = {
    questions: [
        {
            question:
                "When would you purchase a DVD player if you don't already own one?",
            inputLabel: "question_1",
            currentValue: "",
            type: QuestionTypes.textInput,
            rules: yup.string().trim().required()
        },
        {
            question:
                "Would you be interested in software that allows you to have control over profanity, nudity, and violence in movies?",
            inputLabel: "question_2",
            currentValue: question_2,
            type: QuestionTypes.radio,
            rules: yup.string().trim().required().oneOf(question_2)
        },
        {
            question: "What type of DVD player do you own?",
            inputLabel: "question_3",
            currentValue: question_3,
            type: QuestionTypes.select,
            rules: yup.string().trim().required().oneOf(question_3)
        },
        {
            question: "Do you mainly purchase or rent movies you view?",
            inputLabel: "question_4",
            currentValue: question_4,
            type: QuestionTypes.radio,
            rules: yup.string().trim().required().oneOf(question_4)
        },
        {
            question:
                "How much do you spend renting and/or buying movies per month(in USD)?",
            inputLabel: "question_5",
            currentValue: 0,
            type: QuestionTypes.numberInput,
            rules: yup.number().required()
        },
        // {
        //     question: "Type your first name, last name and email address",
        //     inputLabel: ["firstName", "lastName", "email"],
        //     multiple: {
        //         firstName: "",
        //         lastName: "",
        //         email: "",
        //     },
        //     rules: {
        //         firstName: yup.string().trim().required('First name is required'),
        //         lastName: yup.string().trim().required('Last name is required'),
        //         email: yup.string().email('Must be a valid email').trim().required('Email is required'),
        //     }
        // },
        // {
        //     question: "When is your birthday?",
        //     date: new Date(),
        // },
        // {
        //     question: "What is your gender?",
        //     inputLabel: "question_8",
        //     currentValue: question_8,
        //     type: QuestionTypes.radio,
        //     rules: yup.string().trim().oneOf(question_8)
        // },
    ],
    answers: [],
};

const QuestionSlice = createSlice({
    name: "question",
    initialState,
    reducers: {},
});
const questionReducer = QuestionSlice.reducer;
export { questionReducer };
