import React from "react";

interface IInputProps{
    type?: string;
    value?: string;
    label?: string;
}

const Input = React.forwardRef<HTMLInputElement, IInputProps>((props, ref) => {
    return (
        <div>
            {(props.type === 'radio' || props.label) && <label htmlFor={props.value}>{props.value}</label>}
            <input id={props.value} ref={ref} {...props} />
        </div>
    );
});

export default Input;
