import styled from "styled-components";

export const SSelect = styled.select`
    width: calc(100% - 4px);
    display: block;
    padding: 10px;
    outline: 1px solid teal;
    border: none;
    padding: 10px;
`;

export const SelectWrapper = styled.div`
    width: 50%;
    min-width: 400px;
    max-width: 800px;
    margin: 20px 0;
`;
