import styled from "styled-components";

export const Wrapper = styled.div`
    outline: 1px solid teal;
    margin: 15px 0;
    padding: 10px
`;

export const Question = styled.p`
    font-weight: bold;
    margin: 10px 0
`;

export const Answer = styled.p`
    color: var(--green)
`
