import React from "react";
import { useAppSelector } from "../../hooks/redux";
import { useForm } from "react-hook-form";
import { yupResolver } from "@hookform/resolvers/yup";
import * as yup from "yup";

const Survey = () => {
    const { questions } = useAppSelector((state) => state.questionReducer);
    const question = questions[0];
    const questionLabel = question.inputLabel;
    type FormTypes = { [questionLabel: string]: typeof question.currentValue };
    const validationSchema = yup.object().shape({
        [questionLabel]: question.rules,
    });
    const {
        register,
        formState: { errors },
        handleSubmit,
    } = useForm<FormTypes>({ resolver: yupResolver(validationSchema) });

    const onSubmit = (data: FormTypes) => console.log(data);

    return (
        <div>
            <form onSubmit={handleSubmit(onSubmit)}>
                <div>
                    <input type="text" {...register(questionLabel)} />
                    <p>{errors[questionLabel]?.message}</p>
                </div>
                <div>
                    <button>Submit</button>
                </div>
            </form>
        </div>
    );
};

export default Survey;
