import React, { useEffect } from "react";
import { useAppSelector } from "../../hooks/redux";
import * as yup from "yup";
import { useForm } from "react-hook-form";
import { yupResolver } from "@hookform/resolvers/yup";

const Survey = () => {
    const { questions } = useAppSelector((state) => state.questionReducer);
    const question = questions[1];
    const questionLabel = question.inputLabel;
    console.log({ questionLabel });
    // @ts-ignore
    const schema = yup.object().shape({ [questionLabel]: question.rules });
    const {
        register,
        watch,
        handleSubmit,
        formState: { errors },
    } = useForm({ resolver: yupResolver(schema) });

    const onSubmit = (data: any) => console.log(data);

    useEffect(() => {
        const subscription = watch((value, { name, type }) =>
            console.log(value, name, type)
        );
        return () => subscription.unsubscribe();
    }, [watch]);

    return (
        <div>
            <form onSubmit={handleSubmit(onSubmit)}>
                {!Array.isArray(questionLabel) && (
                    <input type="text" {...register(questionLabel)} />
                )}
                {/*<p>{JSON.stringify(errors.question_1)}</p>*/}
                <div>
                    <button>Submit</button>
                </div>
            </form>
        </div>
    );
};

export default Survey;
